	border-radius: 12px 12px 0px 0px;
}

.ct__questions details{
	position: relative;
	z-index: 1;
}

.ct__questions ul li::marker {
  color: #DC4F33;
}

/* SS7 */
.ct__questions .e-n-accordion-item-title[aria-expanded="true"]{
	border-radius: 24px 24px 0px 0px;
}

.ct__questions details{
	position: relative;
	z-index: 1;
}

.ct__questions ul li::marker {
  color: #DC4F33;
}

.ct__questions .e-n-accordion-item-title[aria-expanded="false"] {
  position: relative;
  background-color: rgba(255, 255, 255, 0.03);
  backdrop-filter: blur(48px);
  -webkit-backdrop-filter: blur(48px);
  box-shadow: inset 0 0 16px rgba(255, 255, 255, 0.08);
  color: white;
  font-weight: 600;
  transition: all 0.3s ease;
}

.slider {
  position: relative;
  width: 100%;
  display: grid;
  place-items: center;
  overflow: hidden;
}

.slide-track {
  display: flex;
  width: calc(250px * 20); /* Gấp đôi để có thể cuộn liên tục */
  animation: scroll 100s linear infinite;
}

.slide, .slide2 {
  width: 310px;
}

.slide2 {
  width: 100px;
}

@keyframes scroll {
  0% {
    transform: translateX(0);
  }
  100% {
    transform: translateX(calc(-250px * 20)); /* Cuộn qua toàn bộ các slide */
  }
}

description:
.limited-3-lines {
  display: -webkit-box;
  -webkit-line-clamp: 3;
  -webkit-box-orient: vertical;
  overflow: hidden;
  text-overflow: ellipsis;
}
